// Ordena a lista por sal�rio (decrescente)
void ordenarPorSalario(Funcionario** cabeca) {
    if (*cabeca == NULL || (*cabeca)->proximo == NULL) return;

    int trocou;
    Funcionario* atual;
    Funcionario* proximo = NULL;

    do {
        trocou = 0;
        atual = *cabeca;

        while (atual->proximo != proximo) {
            if (atual->salario < atual->proximo->salario) {
                // Troca os dados
                int tempId = atual->id;
                atual->id = atual->proximo->id;
                atual->proximo->id = tempId;

                char tempNome[50];
                strcpy(tempNome, atual->nome);
                strcpy(atual->nome, atual->proximo->nome);
                strcpy(atual->proximo->nome, tempNome);

                float tempSalario = atual->salario;
                atual->salario = atual->proximo->salario;
                atual->proximo->salario = tempSalario;

                trocou = 1;
            }
            atual = atual->proximo;
        }
        proximo = atual;
    } while (trocou);
}

